    public static (string SvgContent, string Color)? MatchByName(string name)
    {
        if (string.IsNullOrWhiteSpace(name)) return null;
        var normalized = name.Trim().ToLowerInvariant().Replace(" ", "");
        foreach (var (n, svgContent, color) in All)
        {
            var builtInNormalized = n.ToLowerInvariant().Replace(" ", "");
            if (builtInNormalized == normalized || normalized.Contains(builtInNormalized) || builtInNormalized.Contains(normalized))
                return (svgContent, color);
        }
        return null;
    }
